mapper.insert:
insert into m_checkout_entry (
    checkout_entry_serial, partner_company_id, invoice_type, payment_method, payment_number,
    payment_amount, bank_account_id, total_amount, is_rounded, debt, service_fee, remark,
    drawer, creation_date, checkout_date, money_entry_serial, department_id
)
values (
    #{checkoutEntrySerial}, #{partnerCompanyID}, #{invoiceType}, #{paymentMethod}, #{paymentNumber},
    #{paymentAmount}, #{bankAccountID}, #{totalAmount}, #{isRounded}, #{debt}, #{serviceFee}, #{remark},
    #{drawer}, date('now', 'localtime'), #{checkoutDate}, #{moneyEntrySerial}, #{departmentID}
)
