mapper.insert:
insert into e_inbound_product (
    inbound_entry_id, sku_id, quantity, stock_quantity, remark, warehouse_stock_id,
    warehouse_id, tax_rate, unit_price_without_tax, unit_price_with_tax,
    stock_unit_price, return_status, checkout_serial, invoice_serial
)
values (
    #{inboundProductID}, #{inboundEntryID}, #{skuID}, #{quantity}, #{stockQuantity},
    #{remark}, #{warehouseStockID}, #{warehouseID}, #{taxRate}, #{unitPriceWithoutTax},
    #{unitPriceWithTax}, #{stockUnitPrice}, #{returnStatus}, #{checkoutSerial}, #{invoiceSerial}
)

mapper.select:
select i.inbound_entry_id, i.entry_date, i.creation_date, i.total_cost, i.invoice_type, i.drawer, i.partner_company_id, c.abbreviated_name as company_abbreviated_name, c.phone, c.full_name, d.department_id, d.name as department_name, i.warehouse_id, w.name as warehouse_name, i.remark, i.classification, i.shipping_cost, i.shipping_cost_type, i.shipping_quantity, i.shipping_number, i.shipping_method_id, r.abbreviated_name as relevant_company_name, i.shipping_cost_serial, i.return_date, i.return_serial, i.print_times from (((e_inbound_entry as i join c_partner_company as c on i.partner_company_id = c.partner_company_id) join e_department as d on i.department_id = d.department_id) join w_warehouse as w on i.warehouse_id = w.warehouse_id) join c_relevant_company as r on i.shipping_method_id = r.relevant_company_id where (i.entry_date between #{startDate} and #{endDate})
and i.partner_company_id = #{id}
order by i.inbound_entry_id desc

select inbound_product_id, inbound_entry_id, p.sku_id, sku.new_code, sku.old_code,
       sku.unit_name, sku.factory_code, quantity, stock_quantity, remark, warehouse_stock_id,
       warehouse_id, tax_rate, unit_price_without_tax, unit_price_with_tax, stock_unit_price,
       return_status, checkout_serial, invoice_serial
from e_inbound_product as p join (
    select s.sku_id, m.new_code, m.old_code, u.name as unit_name, f.code as factory_code
    from (w_sku as s join w_factory_brand as f on s.factory_brand_id = f.factory_brand_id)
        join (w_model as m join w_measurement_unit as u on m.unit_id = u.measurement_unit_id)
        on s.model_id = m.model_id
) as sku on p.sku_id = sku.sku_id
where inbound_entry_id = #{id}
order by inbound_product_id

select inbound_product_id, inbound_entry_id, quantity, p.remark, tax_rate,
       unit_price_without_tax, unit_price_with_tax, m.new_code, m.old_code
from e_inbound_product as p join (w_sku as s join w_model as m on s.model_id = m.model_id)
    on p.sku_id = s.sku_id
where inbound_entry_id = #{id}

mapper.update:
update e_inbound_entry
set total_cost = #{totalCost},
    invoice_type = #{invoiceType},
    department_id = #{departmentID},
    remark = #{remark}
where inbound_entry_id = #{inboundEntryID}
