mapper.insert:
insert into m_invoice_entry(
    invoice_entry_serial, partner_company_id, invoice_type, invoice_number, total_amount, invoice_amount,
    invoice_indication, is_follow_up_indication, remark, drawer, creation_date, checkout_date, in_or_out,
    invoice_date, invoice_number_date
)
values (
    #{invoiceEntrySerial}, #{partnerCompanyID}, #{invoiceType}, #{invoiceNumber}, #{totalAmount},
    #{invoiceAmount}, #{invoiceIndication}, #{isFollowUpIndication}, #{remark}, #{drawer},
    date('now', 'localtime'), #{checkoutDate}, #{inOrOut}, #{invoiceDate}, #{invoiceNumberDate}
)

mapper.select:
select invoice_entry_serial, i.partner_company_id, c.full_name as company_full_name, invoice_type,
       invoice_number, total_amount, invoice_amount, invoice_indication, is_follow_up_indication, i.remark,
       drawer, creation_date, checkout_date, in_or_out, invoice_date, invoice_number_date, is_modified
from m_invoice_entry as i join c_partner_company as c on i.partner_company_id = c.partner_company_id
where (invoice_entry_serial like #{prefix1}||'%' or invoice_entry_serial like #{prefix2}||'%')
    and (creation_date between #{startDate} and #{endDate})

    and i.invoice_number_date = #{invoiceNumberDate}


    and i.partner_company_ID = #{companyID}


    and i.is_follow_up_indication = #{isFollowUpIndication}


    and i.invoice_number = #{invoiceNumber}


    and i.invoice_type = #{invoiceType}

order by creation_date desc
