mapper.insert:
insert into e_quota_entry(
    quota_entry_id, creation_date, total_amount, invoice_type, drawer, partner_company_id, remark
)
values (
    #{quotaEntryID}, date('now', 'localtime'), #{totalAmount}, #{invoiceType}, #{drawer},
    #{partnerCompanyID}, #{remark}
)

insert into e_quota_product(
    quota_entry_id, sku_id, quantity, remark, tax_rate, unit_price_without_tax, unit_price_with_tax
)
values (
    #{quotaEntryID}, #{skuID}, #{quantity}, #{remark}, #{taxRate}, #{unitPriceWithoutTax},
    #{unitPriceWithTax}
)

mapper.select:
select q.quota_entry_id, q.creation_date, q.total_amount, q.invoice_type, q.drawer, q.partner_company_id, c.abbreviated_name as company_abbreviated_name, c.full_name as company_full_name, c.phone as company_phone, q.remark, q.is_modified from e_quota_entry as q join c_partner_company as c on q.partner_company_id = c.partner_company_id where q.creation_date between #{startDate} and #{endDate}
and q.partner_company_id = #{id}
order by q.quota_entry_id desc

select quota_product_id, quota_entry_id, p.sku_id,
       sku.new_code, sku.old_code, sku.unit_name, sku.factory_code,
       quantity, remark, tax_rate,
       unit_price_without_tax, unit_price_with_tax
from e_quota_product as p join (
    select s.sku_id, m.new_code, m.old_code, u.name as unit_name, f.code as factory_code
    from (w_sku as s join w_factory_brand as f on s.factory_brand_id = f.factory_brand_id)
             join (w_model as m join w_measurement_unit as u on m.unit_id = u.measurement_unit_id)
                  on s.model_id = m.model_id
) as sku on p.sku_id = sku.sku_id
where quota_entry_id = #{id}
order by quota_product_id

select quota_entry_id, total_amount, invoice_type, drawer, remark
from e_quota_entry
where quota_entry_id = #{id}

select quota_product_id, quota_entry_id, quantity, q.remark, tax_rate,
       unit_price_without_tax, unit_price_with_tax, m.new_code, m.old_code
from e_quota_product as q join (w_sku as s join w_model as m on s.model_id = m.model_id)
    on q.sku_id = s.sku_id
where quota_entry_id = #{id}
