mapper.insert:
insert into e_quote_entry(
    quote_entry_id, creation_date, total_amount, invoice_type, tax_rate, drawer, partner_company_id, remark
)
values (
    #{quoteEntryID}, date('now', 'localtime'), #{totalAmount}, #{invoiceType}, #{taxRate}, #{drawer},
    #{partnerCompanyID}, #{remark}
)

insert into e_quote_product(
    quote_entry_id, sku_id, quantity, remark, tax_rate, unit_price_without_tax, unit_price_with_tax
)
values (
    #{quoteEntryID}, #{skuID}, #{quantity}, #{remark}, #{taxRate}, #{unitPriceWithoutTax},
    #{unitPriceWithTax}
)

mapper.select:
select q.quote_entry_id, q.creation_date, q.total_amount, q.invoice_type, q.tax_rate, q.drawer, q.partner_company_id, c.abbreviated_name as company_abbreviated_name, c.full_name as company_full_name, c.phone as company_phone, c.remark as company_remark, q.remark, q.is_modified from e_quote_entry as q join c_partner_company as c on q.partner_company_id = c.partner_company_id where q.creation_date between #{startDate} and #{endDate}
and q.partner_company_id = #{id}
order by q.quote_entry_id desc

select quote_entry_id, total_amount, invoice_type, drawer, remark
from e_quote_entry
where quote_entry_id = #{id}

select quote_product_id, quote_entry_id, quantity, q.remark, tax_rate,
       unit_price_without_tax, unit_price_with_tax, m.code
from e_quote_product as q join (w_sku as s join w_model as m on s.model_id = m.model_id)
    on q.sku_id = s.sku_id
where quote_entry_id = #{id}
