mapper.insert:
insert into e_sales_order_entry(
    sales_order_entry_id, shipment_date, creation_date, total_amount, invoice_type, tax_rate,
    execution_status, drawer, partner_company_id, department_id, warehouse_id, remark
)
values (
    #{salesOrderEntryID}, #{shipmentDate}, date('now', 'localtime'), #{totalAmount},
    #{invoiceType}, #{taxRate}, #{executionStatus}, #{drawer}, #{partnerCompanyID}, #{departmentID},
    #{warehouseID}, #{remark}
)

insert into e_sales_order_product(
    sales_order_entry_id, sku_id, quantity, remark, warehouse_stock_id, warehouse_id,
    tax_rate, unit_price_without_tax, unit_price_with_tax, stock_unit_price
)
values (
    #{salesOrderEntryID}, #{skuID}, #{quantity}, #{remark}, #{warehouseStockID},
    #{warehouseID}, #{taxRate}, #{unitPriceWithoutTax}, #{unitPriceWithTax}, #{stockUnitPrice}
)

mapper.select:
select s.sales_order_entry_id, s.shipment_date, s.creation_date, s.total_amount, s.invoice_type, s.tax_rate,
s.execution_status, s.drawer, s.partner_company_id, c.abbreviated_name as company_abbreviated_name,
c.full_name as company_full_name, c.phone as company_phone, c.remark as company_remark,
s.department_id, d.name as department_name, s.warehouse_id, w.name as warehouse_name, s.remark, s.is_modified
from ((e_sales_order_entry as s join c_partner_company as c on s.partner_company_id = c.partner_company_id)
    join e_department as d on s.department_id = d.department_id)
    join w_warehouse as w on s.warehouse_id = w.warehouse_id
where s.shipment_date between #{startDate} and #{endDate}

    and s.partner_company_id = #{id}

order by s.sales_order_entry_id desc

select sales_order_entry_id, total_amount, invoice_type, execution_status, drawer,
       s.department_id, s.warehouse_id, s.remark, d.name as department_name, w.name as warehouse_name
from (e_sales_order_entry as s join e_department as d on s.department_id = d.department_id)
         join w_warehouse as w on s.warehouse_id = w.warehouse_id
where sales_order_entry_id = #{id}

select sales_order_product_id, sales_order_entry_id, quantity, p.remark, tax_rate,
       unit_price_without_tax, unit_price_with_tax, m.code
from e_sales_order_product as p join (w_sku as s join w_model as m on s.model_id = m.model_id)
                                        on p.sku_id = s.sku_id
where sales_order_entry_id = #{id}
