mapper.insert:
insert into m_shipping_cost_entry(
    shipping_cost_entry_serial, partner_company_id, is_tax_deduction, invoice_number,
    total_amount, invoice_amount, shipping_payment_type, remark, drawer, creation_date,
    checkout_date, in_or_out, invoice_date
)
values (
    #{shippingCostEntrySerial}, #{partnerCompanyID}, #{isTaxDeduction}, #{invoiceNumber},
    #{totalAmount}, #{invoiceAmount}, #{shippingPaymentType}, #{remark}, #{drawer},
    #{creationDate}, #{checkoutDate}, #{inOrOut}, #{invoiceDate}
)
