mapper.select:
select checkout_entry_serial, c.partner_company_id, p.abbreviated_name as company_abbreviated_name, p.full_name as company_full_name, p.phone as company_phone, p.remark as company_remark, invoice_type, payment_method, payment_number, payment_amount, c.bank_account_id, b.name as bank_account_name, total_amount, is_rounded, rounded_amount, debt, service_fee, c.remark, drawer, creation_date, checkout_date, money_entry_serial, return_serial, c.department_id, d.name as department_name, is_verified, is_modified from ((m_checkout_entry as c left join c_bank_account as b on c.bank_account_id = b.bank_account_id) join e_department as d on c.department_id = d.department_id) join c_partner_company as p on c.partner_company_id = p.partner_company_id where (checkout_entry_serial like #{prefix1}||'%' or checkout_entry_serial like #{prefix2}||'%') and (c.checkout_date between #{startDate} and #{endDate})
and c.partner_company_id = #{companyID}
and c.invoice_type = #{invoiceType}
order by creation_date desc

mapper.update:
update m_checkout_entry
set return_serial = #{returnSerial}
where checkout_entry_serial = #{checkoutEntrySerial}

mapper.select:
select m.checkout_entry_serial, m.partner_company_id, c.abbreviated_name, m.invoice_type, p.inbound_product_id as product_id, p.sku_id, s.code, s.unit_name, s.factory_code, p.warehouse_stock_id, p.unit_price_without_tax, p.unit_price_with_tax, p.quantity from m_checkout_entry as m join e_inbound_product as p on m.checkout_entry_serial = p.checkout_serial join c_partner_company as c on m.partner_company_id = c.partner_company_id join sku_full as s on p.sku_id = s.sku_id where (checkout_date between #{startDate} and #{endDate})
and m.partner_company_id = #{companyID}
and s.tree_level like #{treeLevel}||'%'
and factory_code = #{factoryBrand}
and p.warehouse_id = #{warehouseID}
and department_id = #{departmentID}

select m.checkout_entry_serial, m.partner_company_id, c.abbreviated_name, m.invoice_type, p.outbound_product_id as product_id, p.sku_id, s.code, s.unit_name, s.factory_code, p.warehouse_stock_id, p.unit_price_without_tax, p.unit_price_with_tax, p.quantity from m_checkout_entry as m join e_outbound_product as p on m.checkout_entry_serial = p.checkout_serial join c_partner_company as c on m.partner_company_id = c.partner_company_id join sku_full as s on p.sku_id = s.sku_id where (checkout_date between #{startDate} and #{endDate})
and m.partner_company_id = #{companyID}
and s.tree_level like #{treeLevel}||'%'
and factory_code = #{factoryBrand}
and p.warehouse_id = #{warehouseID}
and department_id = #{departmentID}
