mapper.select:
select count(outbound_entry_id)
from e_outbound_entry
where shipment_date = #{date}

mapper.insert:
insert into e_outbound_product (
    outbound_entry_id, sku_id, quantity, stock_quantity, remark, warehouse_stock_id,
    warehouse_id, tax_rate, unit_price_without_tax, unit_price_with_tax, stock_unit_price,
    is_presale
)
values (
    #{outboundEntryID}, #{skuID}, #{quantity}, #{stockQuantity}, #{remark}, #{warehouseStockID},
    #{warehouseID}, #{taxRate}, #{unitPriceWithoutTax}, #{unitPriceWithTax}, #{stockUnitPrice},
    #{isPresale}
)

mapper.select:
select i.outbound_entry_id, i.shipment_date, i.creation_date, i.total_amount, i.delivery_method, i.invoice_type, i.tax_rate, i.drawer, i.partner_company_id, c.abbreviated_name as company_abbreviated_name, c.phone, c.full_name, c.remark as company_remark, d.department_id, d.name as department_name, i.warehouse_id, w.name as warehouse_name, i.remark, i.classification, i.shipping_cost, i.shipping_cost_type, i.shipping_quantity, i.shipping_number, i.shipping_method_id, r.abbreviated_name as relevant_company_name, i.shipping_cost_serial, i.return_date, i.return_serial, i.print_times, i.is_modified from (((e_outbound_entry as i join c_partner_company as c on i.partner_company_id = c.partner_company_id) join e_department as d on i.department_id = d.department_id) join w_warehouse as w on i.warehouse_id = w.warehouse_id) left join c_relevant_company as r on i.shipping_method_id = r.relevant_company_id where (i.shipment_date between #{startDate} and #{endDate}) and i.classification = #{type}
and i.partner_company_id = #{id}
order by i.outbound_entry_id desc

select outbound_product_id, outbound_entry_id, quantity, p.remark, tax_rate,
       unit_price_without_tax, unit_price_with_tax, m.code
from e_outbound_product as p join (w_sku as s join w_model as m on s.model_id = m.model_id)
    on p.sku_id = s.sku_id
where outbound_entry_id = #{id}

mapper.update:
update e_outbound_product
set quantity = #{quantity}
where outbound_product_id = #{outboundProductID}

mapper.select:
select outbound_entry_id
from e_outbound_entry
where partner_company_id = #{companyID} and invoice_type = #{invoiceType}

mapper.update:
update e_outbound_product
set checkout_serial = #{checkoutSerial}
where outbound_product_id = #{outboundProductID}

update e_outbound_product
set invoice_serial = #{invoiceSerial}
where outbound_product_id = #{outboundProductID}

update e_outbound_entry
set shipping_cost_serial = #{shippingCostSerial}
where outbound_entry_id = #{outboundEntryID}

mapper.select:
select i.outbound_entry_id, i.shipment_date, i.creation_date, i.total_amount, i.invoice_type,
       i.drawer, i.partner_company_id, c.abbreviated_name as company_abbreviated_name,
       c.phone, c.full_name, i.remark, i.classification, i.shipping_cost, i.shipping_cost_type,
       i.shipping_quantity, i.shipping_number, i.shipping_method_id,
       r.abbreviated_name as relevant_company_name, i.shipping_cost_serial, i.is_modified
from (e_outbound_entry as i join c_partner_company as c on i.partner_company_id = c.partner_company_id)
    left join c_relevant_company as r on i.shipping_method_id = r.relevant_company_id
where i.shipping_cost_type = #{shippingCostType}

    and i.partner_company_id = #{companyID}

order by i.outbound_entry_id desc

select outbound_product_id, p.outbound_entry_id, p.sku_id, sku_full.code,
       sku_full.unit_name, sku_full.factory_code, quantity, p.stock_quantity, p.remark,
       warehouse_stock_id, p.warehouse_id, p.tax_rate, unit_price_without_tax, unit_price_with_tax,
       stock_unit_price, return_status, checkout_serial, invoice_serial
from (e_outbound_entry as e join (
        e_outbound_product as p join sku_full on p.sku_id = sku_full.sku_id
    ) on e.outbound_entry_id = p.outbound_entry_id)
    join c_partner_company as c on e.partner_company_id = c.partner_company_id
where p.checkout_serial = '' and e.partner_company_id = #{id}

select outbound_product_id, p.outbound_entry_id, p.sku_id, sku_full.code,
       sku_full.unit_name, sku_full.factory_code, quantity, p.stock_quantity, p.remark,
       warehouse_stock_id, p.warehouse_id, p.tax_rate, unit_price_without_tax, unit_price_with_tax,
       stock_unit_price, return_status, checkout_serial, invoice_serial
from (e_outbound_entry as e join (
        e_outbound_product as p join sku_full on p.sku_id = sku_full.sku_id
    ) on e.outbound_entry_id = p.outbound_entry_id)
    join c_partner_company as c on e.partner_company_id = c.partner_company_id
where p.invoice_serial = '' and p.checkout_serial != '' and e.partner_company_id = #{id}

mapper.update:
update e_outbound_product
set stock_quantity = #{stockQuantity},
    stock_unit_price = #{stockUnitPrice},
    is_presale = #{isPresale}
where outbound_product_id = #{outboundProductID}
